/* Code to check subsequential limits - note that exponentialize is used to help handling of trig functions as input */
declare(k, integer);
subseqlimit:lambda([seqterm, subseq], limit(ev(exponentialize(seqterm), n=subseq), k, infinity));
subseqlims:lambda([seqterm], create_list(subseqlimit(seqterm, subseq), subseq, [2*k, 2*k+1, 3*k, 3*k+1, 3*k+2]));

